class Solution(object):
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        word_count = len(words)
        word_len = len(words[0])
        string_len = len(s)
        result = []
        dict_words = {}
        for word in words:
            if word in dict_words:
                dict_words[word] += 1
            else:
                dict_words[word] = 1

        for i in xrange(0, string_len-word_count*word_len+1):
            if self.is_match(s[i:i+word_count*word_len], dict_words, word_len):
                result.append(i)
        return result

    def is_match(self, s, d, word_len):
        dict_words_tmp = {}
        for i in xrange(0, len(s), word_len):
            tmp_word = s[i:i+word_len]
            if tmp_word in d:
                if tmp_word in dict_words_tmp:
                    if dict_words_tmp[tmp_word] == d[tmp_word]:
                        return False
                    else:
                        dict_words_tmp[tmp_word] += 1
                else:
                    dict_words_tmp[tmp_word] = 1
            else:
                return False
        return True


if __name__ == "__main__":
    assert Solution().findSubstring("barfoothefoobarman", ["foo", "bar"]) == [0, 9]
    assert Solution().findSubstring("barfoobarfoobarman", ["foo", "bar"]) == [0, 3, 6, 9]
